    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        pam.setVisible(true);
        
        double a = Double.parseDouble (lblA.getText());
        double b = Double.parseDouble (lblB.getText());
        double c = Double.parseDouble (lblC.getText());
        
        double d = Double.parseDouble (lblD.getText());
        double e = Double.parseDouble (lblE.getText());
        double f = Double.parseDouble (lblF.getText());
        
        int PA = Integer.parseInt(jpnPA.getValue().toString());
        int PB = Integer.parseInt(jpnPB.getValue().toString());
        int PC = Integer.parseInt(jpnPC.getValue().toString());
        
        int PD = Integer.parseInt(jpnPD.getValue().toString());
        int PE = Integer.parseInt(jpnPE.getValue().toString());
        int PF = Integer.parseInt(jpnPF.getValue().toString());
        
        int PN1 = Integer.parseInt(jpnPn1.getValue().toString());
        int PN2 = Integer.parseInt(jpnPn1.getValue().toString());
        
        int DivN1 = Integer.parseInt(jpnDivN1.getValue().toString());
        int DivN2 = Integer.parseInt(jpnDivN2.getValue().toString());
        int DivMi = Integer.parseInt(jpnDivMi.getValue().toString());
        
        double pFinal = Double.parseDouble(jpnPFl.getValue().toString());        
        double PARTIC = Double.parseDouble(jpnPartic.getValue().toString());
        
        double n1 = ((a*PA+b*PB+c*PC)/DivN1);
        double n2 = ((d*PD+e*PE+f*PF)/DivN2);
        
        lblA1.setText(String.format("%.1f",a));
        lblB1.setText(String.format("%.1f",b));
        lblC1.setText(String.format("%.1f",c));
        lblD1.setText(String.format("%.1f",d));
        lblE1.setText(String.format("%.1f",e));
        lblF1.setText(String.format("%.1f",f));       
        
        lblN1.setText(String.format("%.2f",n1));
        lblN2.setText(String.format("%.2f",n2));
        
        lblN1a.setText(String.format("%.1f",n1));
        lblN2a.setText(String.format("%.1f",n1));
        
        double mi =  ((n1*PN1+n2*PN2)/DivMi)+PARTIC;
        double mf = (mi+pFinal)/2;
        
        lblMia.setText(String.format("%.1f",mi));
       
        lblMi.setText(String.format("%.2f",mi));
        lblMf.setText(String.format("%.2f",mf));
              
    }//GEN-LAST:event_jButton1ActionPerformed

